mapper.insert:

INSERT INTO 
			game(
		game_no
	,	game_name_ko
	,	game_name_en
	,	game_people
	,	game_age
	,	game_time
	,	game_difficulty
	,	game_description
	,	game_guide
	,	theme_no
	)
VALUES(
		seq_game_no.NEXTVAL
   ,	#{gameNameKo}
   ,	#{gameNameEn}
   ,	#{gamePeople}
   ,	#{gameAge}
   ,	#{gameTime}
   ,	#{gameDifficulty}
   ,	#{gameDescription}
   ,	#{gameGuide}
   ,	#{themeNo}

   )

mapper.select:
SELECT game_no , game_name_ko , game_name_en , game_path_thumbnail , game_people , game_age , game_time , game_difficulty , t.theme_no , theme_name , game_description , game_guide
FROM game g, theme t WHERE game_no = #{gameNo}
AND t.theme_no = g.theme_no

mapper.delete:

DELETE FROM
	game_bookmark
WHERE
	user_no=#{userNo}
AND	
	game_no=#{gameNo}
